import { Component } from '@angular/core';

import { userDataService } from './services/userData.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor (private userDataService : userDataService) {}

  onSetToInactive(id: number) {
    this.userDataService.inactiveUsers.push(this.userDataService.activeUsers[id]);
    this.userDataService.activeUsers.splice(id, 1);
  }

  onSetToActive(id: number) {
    this.userDataService.activeUsers.push(this.userDataService.inactiveUsers[id]);
    this.userDataService.inactiveUsers.splice(id, 1);
  }
}
<div class="container">
  <div class="row">
    <div class="col-xs-12 col-md-8 col-md-offset-2">
      <app-active-users

        (userSetToInactive)="onSetToInactive($event)"></app-active-users>
      <app-inactive-users

        (userSetToActive)="onSetToActive($event)"></app-inactive-users>
    </div>
    <!--
      Optimize this app by adding a UsersService which manages the active and inactive users.

      Also add a CounterService which counts the number of active->inactive and inactive->active actions.

      <div class="col-xs-12 col-md-8 col-md-offset-2">
      <app-active-users
        [users]="activeUsers"
        (userSetToInactive)="onSetToInactive($event)"></app-active-users>
      <app-inactive-users
        [users]="inactiveUsers"
        (userSetToActive)="onSetToActive($event)"></app-inactive-users>
    </div>
    -->
  </div>
</div>
